mapper.select:
select b.* from( select a.*, row_number() over (order by a.inqry_no desc) rnum from ( SELECT inqry_no , frcs_id , inqry_ttl , inqry_cn , inqry_ymd , inqry_yn , table_name FROM FRCS_INQUIRY WHERE 1 = 1 and frcs_id = #{frcsId}
order by inqry_no desc ) a ) b
where b.rnum >= #{startRow} and b.rnum <= #{endRow}

mapper.insert:

	select seq_frcsinquiry.nextval from dual

INSERT INTO frcs_inquiry (
    inqry_no
    , frcs_id
    , inqry_ttl
    , inqry_cn
    , inqry_ymd
    , inqry_yn
    , table_name
) VALUES (
    #{inqryNo}
  , #{frcsId}
  , #{inqryTtl}
  , #{inqryCn}
  , sysdate
  , 'N'
  , 'frcsInquiry'
)

mapper.update:
	UPDATE frcs_inquiry
	SET
	    inqry_ttl = #{inqryTtl}
	  , inqry_cn = #{inqryCn}
	WHERE
        inqry_no = #{inqryNo}

mapper.insert:
INSERT INTO attach (
  ATTACH_NO
  , FILE_NO
  , ATTACH_ORGNAME
  , ATTACH_SAVENAME
  , ATTACH_MIME
  , ATTACH_SIZE
  , TABLE_NAME
  , TABLE_PK
)
VALUES (
  seq_attach.nextval
  , #{fileNo}
  , #{attachOrgname}
  , #{attachSavename}
  , #{attachMime}
  , #{attachSize}
  , 'frcsInquiry'
  , #{tablePk}
